mapper.update:
update book0320 
set bname=#{param1}, name=#{param2}, bdate=#{param3}, bprice=#{param4}, bimage=#{param5}, 
    bcnt=#{param6}, groups=#{param7}, step=#{param8}, indent=#{param9} 
where bnumber=#{param10}

mapper.select:
select * from (
  select rownum rn, A.* from (
    select bnumber, bname, name,to_char(bdate, 'yyyy-MM-dd')bdate, bprice,bimage ,bcnt , groups, step, indent 
    from book0320 order by groups DESC, step asc
  ) A
)
where rn between #{start} AND #{end}
